function calcReadings()
{
    tray.frontEndLpf.value = Math.round(125e6 / (8 * twoToN(tray.frontEndLpfShiftR.value) ) ) / 1000;
    tray.clkFreq.value = Math.round(125e3 / twoToN(tray.clkDivide.value)) / 1000;
    tray.notchFreq.value = Math.round(125e6 / (tray.notchFilterMemory.value * twoToN(tray.clkDivide.value) ) ) / 1000;
    tray.gainSign.value = 1;
    if (tray.gmult.value < 0) tray.gainSign.value = -1;
    tray.gain.value = Math.round(200.0 * Math.log10(tray.gainSign.value * tray.gmult.value * twoToN(tray.gainShiftL.value) ) ) / 10;
    tray.readbackLpf.value = Math.round(125e8 / (8 * twoToN(tray.readbackLpfShiftR.value) * twoToN(tray.clkDivide.value))) / 100;
}
function twoToN(n)
{
    pow = 1;
    for (let ii = 0; ii < n; ++ii) pow = pow * 2;
    return pow;
}

