function normRead(pynqInt)
{
    let normVal = pynqInt;
    let two31Minus1 = 2147483647;
    let two31 = 2147483648;
    let two32 = 4294967296;
    normVal = pynqInt;
    if (pynqInt >= two31) normVal = pynqInt - two32;
    normVal = normVal / two31Minus1;   
    return normVal;
}
function setBeamParam()
{
    ival = context.get("ival");
    qval = context.get("qval");
    tray.thetaBeam.value = Math.round(Math.atan2(qval, ival) * 180.0 * 100/ Math.PI) / 100;
    tray.magBeam.value   = Math.round(Math.sqrt(ival * ival + qval * qval) * 10000)/ 10;
    return;
}

