{
    magVecHistory = [];
    global.set('restartFftPlot', false);
}
magVecHistory.push([...msg.payload.value]);
while (magVecHistory.length > tray.fftAverages.value) magVecHistory.shift();
fftTray.fftVec.value[0] = [];
fftTray.fftVec.value[1] = [];
for (let ii = 0; ii < npts; ++ii)
{
    let avg = 0;
    for (ivec = 0; ivec < magVecHistory.length; ++ivec)
    {
        avg = avg + magVecHistory[ivec][ii];
    }
    avg = avg / magVecHistory.length;
    fftTray.fftVec.value[1].push(avg);
}
context.set('magVecHistory',magVecHistory);

var magChart = [
    {
        series : ["Spectrum"],
        data   : [[]],
        labels : [""]
    }
];
