{
    fftTray.fftVec.value[0].push(Math.round(125e6 * ii / raise2toN(tray.waveformClkDivide.value + tray.waveformSamplePower.value)));
    fftTray.fftVec.value[1][ii] = Math.round(1000 * Math.log10(fftTray.fftVec.value[1][ii])  +  100 * (15 - 90.308 - tray.gain.value)) / 100;
    magChart[0].data[0].push({"x":fftTray.fftVec.value[0][ii],"y":fftTray.fftVec.value[1][ii]});
}

return [{topic:"fftmag",payload:true},{topic:'MagChart',payload: magChart}];


